import React, { useEffect, useState } from "react";
import { useLocation, useHistory } from "react-router-dom";
import axios from "../API/axios.js";
import useAuth from "../Auth/useAuth";
import closeBtnpic from "../Svg/close.svg";
import Alert from "../admin_home/Components/Alert";

const Email_Checker = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

function Modal({ setLoginmodal }) {
  const { setAuth } = useAuth();
  const [alert, setalert] = useState({
    mode: false,
    message: "",
    type: "",
  });
  // navigate and set or exist path take
  const location = useLocation();
  const navigate = useHistory();

  const [reset, setReset] = useState(false);
  const [email, setEmail] = useState("");
  // const [validEmail, setValidEmail] = useState(false);

  const [password, setPassword] = useState("");
  const [oldpassword, setoldPassword] = useState("");
  const [newpassword, setnewPassword] = useState("");
  const [validPassword, setValidPassword] = useState(false);

  const [errMsg, setErrMsg] = useState("");
  const [success, setSuccess] = useState(false);

  // // validation in all fields
  // useEffect(() => {
  //   setValidEmail(Email_Checker.test(email));
  // }, [email]);

  useEffect(() => {
    setValidPassword(true);
  }, [password]);

  // useEffect(() => {
  //   setErrMsg("");
  // }, [email, password]);

  // handling submit
  const handleReset = async (e) => {
    e.preventDefault();
    // console.log(email);

    // if button enabled with JS hack
    const e1 = Email_Checker.test(email);
    // const e2 = validPassword;
    // console.log(e1, e2);
    if (!e1) {
      // setErrMsg("Invalid Entry");
      // console.log(errMsg);
      setalert({
        mode: true,
        message: "Invalid Entry",
        type: "bg-[red]",
      });
      return;
    }
    try {
      // console.log("Inside try block");
      const response = await axios.patch(
        "/users/resetpasswd",
        JSON.stringify({ email, oldpassword, newpassword }),
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true,
        }
      );

      // navigate to where it comes from
      setoldPassword("");
      setnewPassword("");
      setReset(false);
      console.log(response);
      if (response.data) {
        setalert({
          mode: true,
          message: "Reset Password",
          type: "bg-[green]",
        });
      }
    } catch (err) {
      if (!err?.response) {
        setalert({
          mode: true,
          message: "No server Responce ",
          type: "bg-[red]",
        });
      } else if (err.response?.status === 409) {
        setalert({
          mode: true,
          message: "User not available",
          type: "bg-[red]",
        });
      } else {
        setalert({
          mode: true,
          message: "User Not Found",
          type: "bg-[red]",
        });
      }
    }
  };
  const handleLogin = async (e) => {
    e.preventDefault();
    console.log(email);

    // if button enabled with JS hack
    const e1 = Email_Checker.test(email);
    const e2 = validPassword;
    console.log(e1, e2);
    if (!e1 || !e2) {
      // setErrMsg("Invalid Entry");
      console.log(errMsg);
      setalert({
        mode: true,
        message: "Invalid Entry",
        type: "bg-[red]",
      });
      return;
    }
    try {
      console.log("Inside try block");
      const response = await axios.post(
        "/auth/login",
        JSON.stringify({ email, password }),
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true,
        }
      );
      console.log("After request", response);

      console.log(JSON.stringify(response?.data));
      const userId = response.data.userId;
      const name = response.data.name;
      const newEmail = response.data.email;
      const mobileno = response.data.mobileno;
      const accessToken = response.data.accessToken;
      const role = response.data.role;

      // setAuth on login
      setAuth({ userId, name, email: newEmail, mobileno, role, accessToken });

      // navigate to where it comes from
      setLoginmodal(false);
      navigate.push(
        role === 0
          ? "/user/profile"
          : role === 1
          ? "/admin/home"
          : "/unauthorized",
        {
          replace: true,
        }
      );
    } catch (err) {
      if (!err?.response) {
        setalert({
          mode: true,
          message: "No server Responce ",
          type: "bg-orange-400",
        });
      } else if (err.response?.status === 409) {
        setalert({
          mode: true,
          message: "User not available",
          type: "bg-orange-400",
        });
      } else {
        setalert({
          mode: true,
          message: "User Not Found",
          type: "bg-orange-400",
        });
      }
    }
  };
  return (
    <div
      className="relative z-10"
      aria-labelledby="modal-title"
      role="dialog"
      aria-modal="true"
    >
      <div
        className="fixed inset-0 bg-gray-600 bg-opacity-75 transition-opacity"
        onClick={() => setLoginmodal(false)}
      />

      <div className="flex fixed left-[35%] min-w-[30%] max-w-[31%]   transform overflow-hidden  p-10  bg-white rounded-lg   flex-col    md:mt-0 ">
        <div className="flex">
          <h2 className="grow h-14 text-black text-3xl text-center font-medium title-font mb-2">
            Add user
          </h2>
          <div class="flex-none ">
            <img
              src={closeBtnpic}
              alt=""
              className=" cursor-pointer min-h-[35px] min-w-[35px] mt-1"
              onClick={() => setLoginmodal(false)}
            />
          </div>
        </div>
        {alert.mode ? <Alert alert={alert} setalert={setalert} /> : ""}
        {!reset ? (
          <form>
            <div className="relative mb-4">
              <label htmlFor="email" className="leading-7 text-sm text-black">
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                onChange={(e) => setEmail(e.target.value)}
                value={email}
                className="w-full bg-white rounded border border-gray-300 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out"
              />
            </div>
            <div className="relative mb-4">
              <label
                htmlFor="password"
                className="leading-7 text-sm text-black"
              >
                Password
              </label>
              <input
                type="password"
                id="password"
                name="password"
                onChange={(e) => setPassword(e.target.value)}
                value={password}
                className="w-full bg-white rounded border border-gray-300 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out"
              />
            </div>
            <div className="flex justify-center">
              <button
                onClick={handleLogin}
                className="text-black bg-gradient-to-r from-[#FF6200] to-[#FDB777] border-2 hover:bg-gradient-to-t hover:from-[#FDB777] hover:to-[#FF6200]  border-black font-semibold text-xl  mt-4 py-2 px-8 focus:outline-none rounded "
              >
                Login
              </button>
            </div>
          </form>
        ) : (
          <form>
            <div className="relative mb-4">
              <label
                htmlFor="email"
                className="leading-7 text-sm text-gray-600"
              >
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                onChange={(e) => setEmail(e.target.value)}
                value={email}
                className="w-full bg-white rounded border border-gray-300 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out"
              />
            </div>
            <div className="relative mb-4">
              <label
                htmlFor="password"
                className="leading-7 text-sm text-gray-600"
              >
                Old Password
              </label>
              <input
                type="password"
                id="password"
                name="password"
                onChange={(e) => setoldPassword(e.target.value)}
                value={oldpassword}
                className="w-full bg-white rounded border border-gray-300 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out"
              />
            </div>
            <div className="relative mb-4">
              <label
                htmlFor="password"
                className="leading-7 text-sm text-gray-600"
              >
                Set New Password
              </label>
              <input
                type="password"
                id="password"
                name="password"
                onChange={(e) => setnewPassword(e.target.value)}
                value={newpassword}
                className="w-full bg-white rounded border border-gray-300 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out"
              />
            </div>

            <button
              onClick={handleReset}
              className="text-white bg-indigo-600 border-0 py-2 px-8 focus:outline-none hover:bg-indigo-600 rounded text-lg"
            >
              Reset Password
            </button>
          </form>
        )}
      </div>
    </div>
  );
}
export default Modal;
