import React from 'react';
import { Box, TextField, Button, styled, Typography } from '@mui/material';
import { useState } from 'react';

const Component = styled(Box)`
    width: 400px;
    margin: auto;
    box-shadow: 5px 2px 5px 2px rgb(0 0 0/ 0.6);
`;
const Image = styled('img')({
    width: 100,
    margin: 'auto',
    display: 'flex',
    padding: '50px 0 0'
});

const Wrapper = styled(Box)`
    padding: 25px 35px;
    display: flex;
    flex = 1;
    flex-direction: column;
    & > div, & > button, & > p {
        margin-top: 20px;
    }
`;

const LoginButton = styled(Button)`
    text-transform: none;
    background: #FB641B;
    color: #fff;
    height: 48px;
    border-radius: 2px;
`;

const SignupButton = styled(Button)`
    text-transform: none;
    background: #fff;
    color: #2874f0;
    height: 48px;
    border-radius: 2px;
    box-shadow: 0 2px 4px 0 rgb(0 0 0/ 20%);
`;

const Text = styled(Typography)`
    color: #878787;
    font-size: 16px;
`;

const Login = () => {


    const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png'
    const [account, setAccount] = useState('login');

    const toggle = () => {
        if(account==='login') setAccount('signup');
        else setAccount('login');
    };

    const [signup, setSignup] = useState({name: '', username: '', password: ''});

    const onInputChange = (e) => {
        setSignup({...signup, [e.target.name]: e.target.value});
    };

    const signupUser = () => {

    };

    return (
        <div style={{ marginTop: 64 }}>
        <Component>
            <Box>
                <Image src={imageURL} alt="login" />
                    {
                        account === 'login' ?
                        <Wrapper>
                            <TextField variant='standard' label="Enter username"/>
                            <TextField variant='standard' label="Enter password"/>
                            <LoginButton variant="contained">Login</LoginButton>
                            <Text style = { {textAlign: 'center'} }>OR</Text>
                            <SignupButton onClick={() => toggle()}>Create an account</SignupButton>
                        </Wrapper>
                        :
                        <Wrapper>
                            <TextField variant='standard' onChange={(e) => onInputChange(e)} name='name' label="Enter name"/>
                            <TextField variant='standard' onChange={(e) => onInputChange(e)} name='username' label="Enter username"/>
                            <TextField variant='standard' onChange={(e) => onInputChange(e)} name='password' label="Enter password"/>
                            <SignupButton onClick={() => signupUser()}>Signup</SignupButton>
                            <Text style = { {textAlign: 'center'} }>OR</Text>
                            <LoginButton variant="contained" onClick={() => toggle()}>Already have an account?</LoginButton>
                        </Wrapper>
                    }
            </Box>
        </Component>
        </div>
    );
}

export default Login;
