import React from 'react';
import Navbar from './components/Navbar';
import './App.css';
import Home from './components/pages/Home';
import { BrowserRouter, Route, Switch, useHistory } from 'react-router-dom';
import Login from './components/pages/Login';
import Admin_Pages from './admin_home/Pages/Admin_Pages';
import User_Page from './user_home/Pages/User_Page';
import useAuth from './Auth/useAuth';
// import RequireAuth from './Auth/RequireAuth';

const RequireAuth = ({accessRole}) => {
  const {auth} = useAuth();
  const history = useHistory();

  return (
      auth.email && (auth.role === accessRole) 
          ? <Route path='/user/:id' exact component={User_Page}></Route>
          : auth.email ?  history.push("/unauthorized")
          : history.push("/")
  )
}

function App() {
  return (
    <>
      <BrowserRouter>
        <Navbar />
          <Switch>
          <Route path='/' exact component={Home} />
          <Route element={<RequireAuth accessRole={0} />}></Route>
          <Route path='/user/:id' exact component={User_Page} />
          <Route path='/admin/:id' exact component={Admin_Pages} />
          <Route path='/services' exact component={User_Page} />
          <Route path='/products' exact component={Admin_Pages} />
          <Route path='/sign-up' component={Login} />
          {/* <Route path='/fuck' exact component={Attendance}/> */}
          {/* <Route path='/user/menu' component={UserMenu} /> */}
        </Switch>
      </BrowserRouter>
    </>
  );
}

export default App;
